mapper.select:
select user_no as userNo, user_id as userId, user_name as userName, user_gender as userGender, user_email as userEmail, user_tel as userTel, user_start_time as StartTime, user_end_time as EndTime, user_attendance_date as userAttDate, class_attendance_date as classAttDate, program_no as programNo, membership_payment_status as membership, program_name as programName from ( select row_number() over (order by user_no asc) row_numbers, user_no, user_id, user_name, user_gender, user_email, user_tel, user_start_time, user_end_time, user_attendance_date, class_attendance_date, program_no, membership_payment_status, program_name from ( select A.user_no, A.user_id, A.user_name, A.user_gender, A.user_email, A.user_tel, null as user_start_time, null as user_end_time, C.class_attendance_date, null as user_attendance_date, C.program_no, null as membership_payment_status, D.program_name from users A, user_class_attendances C , fitness_programs D where A.user_no = C.user_no and C.program_no = D.program_no and C.class_attendance_date is not null
and A.user_name = #{keyword}
and A.user_no = #{keyword}
and A.user_tel = #{keyword}
and D.program_name = #{programInfo}
union select A.user_no, A.user_id, A.user_name, A.user_gender, A.user_email, A.user_tel, B.user_start_time, B.user_end_time, null as class_attendance_date, B.user_attendance_date, null, E.membership_payment_status, null
from users A, user_attendances B , membership_histories E where A.user_no = B.user_no
and A.user_no = E.user_no and E.membership_payment_status = 'Y' and B.user_attendance_date is not null
and A.user_name = #{keyword}
and A.user_no = #{keyword}
and A.user_tel = #{keyword}
and D.program_name = #{programInfo}
)) WHERE row_numbers between #{begin} and #{end}

select
	count(*)
from
	users , fitness_programs
where
	user_deleted = 'N'



  		 		and user_name = #{keyword}


